      $(document).ready(function() {
        $(window).scroll(function() {
          var height = $('.navbar').height();
          var scrollTop = $(window).scrollTop();

          if (scrollTop >= 80) {
            $('.navbar').addClass('top');
            $('.logoheader-top').addClass('showHeader');
          } else {
            $('.navbar').removeClass('top');
            $('.logoheader-top').removeClass('showHeader');
          }
        });
      });

